(function($){
	// imaga change interval (ms)
	var interval = 10000;
	// effect speed
	var duration = 500;

	var animationEasing = 'easeInQuad';
	
	// timer flag
	var timer = false;
	
	// current image index
	var current = 0;
	
	// on animation flag
	var on_animation = false;
	
	// main images jquery objects
	var $mainList;
	
	// thumbnail images jquery objects
	var $thumbList;
	
	// slider object
	var $slider;
	
	var isSmartPhone = false;
	
	$(document).ready(function(){

		// スマートフォン判定
		var agent = navigator.userAgent;
 		if(agent.search(/iPhone/) != -1 
				|| agent.search(/iPad/) != -1
				|| agent.search(/Android/) != -1){
			isSmartPhone = true;
		}


		// cached object
		$mainList = $("#mainVisualMain li");
		$thumbList = $("#mainVisualThumb li");

		// スライダーオブジェクト生成
		$slider = $("<div></div>").css({
			position: 'absolute', 
			top: 0, 
			left: 0, 
			width: 0, 
			height: '1px', 
			backgroundColor: '#7d5b11'
		}).appendTo($("#mainVisualSlider"));


		// start timer
		function startTimer() {
			stopTimer();
			timer = true;
			$slider.css({width:0}).animate(
				{width: '100px'},
				{
					duration: interval,
					easing: 'linear',
					complete:function(){
						var next = current + 1;
						if (next >= $mainList.size()) {
							next = 0;
						}
						switchImg(next);
					}
				}
			);
		}

		// stop timer
		function stopTimer() {
			if (timer) {
				$slider.stop();
				timer = false;
			}
		}

		// switch image
		function switchImg(next) {
//			if (on_animation) {
//				return;
//			}
			if (current != next) {
				on_animation = true;
				var nextLi = $mainList.eq(next);
				var currLi = $mainList.eq(current);
//				for (var i = 0; i < $mainList.size(); i ++) {
//					if (current == i) {
//					} else if (next == i) {
//					} else {
//						$mainList.eq(i).css('display', 'none');
//					}
//				}

				if (on_animation) {
					$mainList.stop();
				}
				currLi.css(
					{display: 'block', zIndex: 1}
				);
				nextLi.css(
					{display: 'block', zIndex: 2, opacity: 0}
				).animate(
					{opacity: 1.0},
					{
						duration: duration,
						easing: animationEasing,
						complete: function() {
							on_animation = false;
							$mainList.not(nextLi).css({
								display: 'none',
								zIndex: 1
							});
							startTimer();
						}
					}
				);
			}
			current = next;
			$thumbList.removeClass('active');
			$thumbList.eq(next).addClass('active');

		}

		// マウスオーバー処理
		$thumbList.mouseover(function(){
			stopTimer();
			var index = $thumbList.index($(this));
			switchImg(index);
		});
		
		startTimer();

	});
	
	

}(jQuery));

